import React from 'react';
import { withRouter } from 'react-router-dom';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { student_add } from 'Sessions/action.js';
import moment from 'moment';

import AddStudentPresenter from './AddStudentPresenter';

class AddStudent extends React.Component {
    static propTypes = {
        user: PropTypes.objectOf(PropTypes.any).isRequired,
        dispatch: PropTypes.func.isRequired,
    };

    state = {
        showPopup: false,
    }

    goBack = () => {
        this.props.history.goBack();
    };

    handleSubmit = (e, { name, birth, gender }) => {
        e.preventDefault();
        const { user } = this.props;
        const { dispatch } = this.props;

        const icon = this.props.match.params.icon;
        if (icon === undefined) {
            this.text = '아이콘을 선택해주세요.';
            this.setState({
                showPopup: true,
            })
            return false;
        }

        if (!name || !birth || !gender) {
            this.text = '빠진 부분 없이 입력해 주세요.';
            this.setState({
                showPopup: true,
            })
            return false;
        }

        if (birth.length !== 8 || isNaN(birth) || !moment(birth, 'YYYYMMDD', true).isValid()) {
            this.text = '올바른 생일을 입력해 주세요.(예시: 20100813)';
            this.setState({
                showPopup: true,
            })
            return false;
        }

        if (gender !== '남자' && gender !== '여자') {
            this.text = '올바른 성별을 입력해 주세요.(예시; 여자)';
            this.setState({
                showPopup: true,
            })
            return false;
        }

        gender = (gender === '남자' ? 'm' : 'f');
        birth = moment(birth).format('YYYY-MM-DD');

        console.log(user);
        dispatch(student_add(name, birth, gender, icon, user.user.u_id));

        return true;
    }

    componentDidMount() {
        const { user } = this.props;
        console.log(user);
        if (!user.user.u_id) {
            this.text = '잘못된 접근입니다.';
            this.setState({
                showPopup: true,
            })
            this.props.history.push('/root/signin');
        }
    }

    componentDidUpdate() {
        const { user } = this.props;
        console.log(user);
        if (user.student.s_id && user.user.u_id) {
            this.text = '사용자 추가를 성공했습니다. 검사 페이지로 이동합니다.';
            this.setState({
                showPopup: true,
            })
            this.props.history.push('/diagnose/sweep');
        }

        if (user.response.data && user.response.data.code == 2) {
            this.text = '존재하지 않는 회원입니다.';
            this.setState({
                showPopup: true,
            })
        }
    }

    onButtonTouchEnd = () => {
        this.setState({
            showPopup: false,
        });
    }

    render() {
        return (<
            AddStudentPresenter
            handleSubmit={this.handleSubmit}
            iconNum={this.props.match.params.icon}
            goBack={this.goBack}
            onButtonTouchEnd={this.onButtonTouchEnd}
            showPopup={this.state.showPopup}
            text={this.text}
        />);
    }
}

function mapStateToProps(state) {
    return { user: state.user }
}

export default connect(mapStateToProps)(withRouter(AddStudent));
