import { soundURL, D_tutorial } from 'api';

export default tutorial = (data) => {
    this.voice = [];
    this.voice_desc = [];
    this.swp = [new Audio(soundURL + data.sample_ques.ques_path2), new Audio(soundURL + data.sample_ques.ques_path1)];
    this.buttonSound = [new Audio(soundURL + data.sample_ques.ques_path2), new Audio(soundURL + data.sample_ques.ques_path1)];
    for (let i = 0; i < 4; i++) {
        this.voice.push(new Audio(soundURL + data.voice[i].voc_path));
        this.voice_desc.push(data.voice[i].voc_desc);

        this.voice[i].addEventListener('ended', () => {
            this.setState({
                TTobaki: TTobak.ttobak1_1,
                gameState: 'tutorial',
                tutorialState: this.voice_desc[i],
            });
        });
    }

    const swpUp = (cnt) => {
        if (cnt >= 2) {
            setTimeout(() => {
                this.voice[1].play();
                this.tutorialCnt = 0;
                this.setState({
                    TTobaki: TTobak.ttobak3_2,
                    gameState: false,
                })
            }, 1000);
            return cnt;
        }

        this.swp[0].play();
        this.setState({
            UpButton: D1.d1_UpButton_DOWN,
        });
        setTimeout(() => {
            this.setState({
                UpButton: D1.d1_UpButton_UP,
            });
            setTimeout(() => {
                cnt = swpUp(++cnt);
            }, 1000);
        }, 500);
    };

    this.voice[0].addEventListener('ended', () => {
        this.setState({
            TTobaki: TTobak.ttobak1_1,
        });

        setTimeout(() => {
            swpUp(0);
        }, 1000);
    })

    this.voice[3].addEventListener('ended', async () => {
        await D_tutorial.answer(this.props.user.student.s_id, idx_txt);
        this.setState({
            initState,
        });
        this.newRequest();
    })

    setTimeout(() => {
        this.voice[0].play();
        this.setState({
            TTobaki: TTobak.ttobak3_2,
            gameState: false,
        })
    }, 1500);

    // this.voice = null;
    // this.newRequest();
}

export const tutorialButtonEvent = (id) => {
    const swpDown = (cnt) => {
        if (cnt >= 2) {
            setTimeout(() => {
                this.voice[2].play();
                this.tutorialCnt = 0;
                this.setState({
                    TTobaki: TTobak.ttobak3_2,
                    gameState: false,
                })
            }, 1200);
            return cnt;
        }

        this.swp[1].play();
        this.setState({
            DownButton: D1.d1_DownButton_DOWN,
        });
        setTimeout(() => {
            this.setState({
                DownButton: D1.d1_DownButton_UP,
            });
            setTimeout(() => {
                cnt = swpDown(++cnt);
            }, 1000);
        }, 500);
    };

    if (id === UP && this.state.tutorialState === this.voice_desc[1]) {
        this.setState({
            tutorialState: this.voice_desc[2],
        })
        setTimeout(() => {
            swpDown(0);
        }, 2000);
    } else if (id === DOWN && this.state.tutorialState === this.voice_desc[2]) {
        setTimeout(() => {
            this.setState({
                TTobaki: TTobak.ttobak3_2,
                gameState: false,
                tutorialState: this.voice_desc[3],
            })
            this.voice[3].play();
        }, 2000);
    }
}
